mapper.select:
select id ,`username` ,super_admin ,create_date ,avatar ,`status` FROM (select id,AES_DECRYPT(username, #{key}) as username,super_admin,create_date,avatar,`status`,del_flag,dept_path,creator from boot_sys_user) as boot_sys_user where del_flag = 0 and create_date between #{pageQuery.startTime} and #{pageQuery.endTime}
and `username` like #{user.username}
order by id desc limit ${pageQuery.pageIndex},${pageQuery.pageSize}

select count(1) as num
FROM (select AES_DECRYPT(`username`, #{key}) as username,del_flag,create_date,dept_path,creator from boot_sys_user) as boot_sys_user
where del_flag = 0
and create_date between #{pageQuery.startTime} and #{pageQuery.endTime}

    and `username` like #{user.username}

select count(1)
from (select id,AES_DECRYPT(`username`, #{key}) as username,del_flag from boot_sys_user) as boot_sys_user
where del_flag = 0
and username = #{user.username}

    and id  <>  #{user.id}

select count(1)
from boot_sys_user
where AES_DECRYPT(`username`, #{key}) = #{username}
