INSERT INTO `his_config_info` VALUES (17, 2157, 'application-common.yaml', 'LAOKOU_GROUP', '', '# jasypt\njasypt:\n  encryptor:\n    password: 5201314wumeihua\n\n# spring\nspring:\n  # task\n  task:\n    execution:\n      pool:\n        core-size: 9\n        keep-alive: 60s\n      thread-name-prefix: laokou-task-\n  # https://codecentric.github.io/spring-boot-admin/current\n  boot:\n    # admin\n    admin:\n      client:\n        url: http://127.0.0.1:5000/monitor\n        # root\n        username: ENC(esZnNM2DrSxZhgTOzu11W2fVsJDDZ1b12aPopMMHCS7lF5+BJun9ri6y5pTUdj6L)\n        # laokou123\n        password: ENC(XVR9OF604T3+2BINpvvCohjr7/KM/vuP3ZgYpu+FX/h3uogFI3sh26h8wHPBE+rj)\n  cloud:\n    # 解决集成sentinel，openfeign启动报错，官方下个版本修复\n    openfeign:\n      compression:\n        response:\n          enabled: true\n        request:\n          enabled: true\n      # FeignAutoConfiguration、OkHttpFeignLoadBalancerConfiguration、OkHttpClient#getClient、FeignClientProperties、OptionsFactoryBean#getObject\n      # 在BeanFactory调用getBean()时，不是调用getBean，是调用getObject(),因此，getObject()相当于代理了getBean(),而且getObject()对Options初始化，是直接从openfeign.default获取配置值的\n      okhttp:\n        enabled: true\n      circuitbreaker:\n        enabled: true\n      httpclient:\n        enabled: false\n      client:\n        config:\n          default:\n            connectTimeout: 120000 #连接超时\n            readTimeout: 120000 #读取超时\n      lazy-attributes-resolution: true\n    # sentinel\n    sentinel:\n      eager: true #开启饥饿加载，直接初始化\n      transport:\n        port: 8769\n        dashboard: 127.0.0.1:8081\n\n# actuator\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n  endpoint:\n    health:\n      show-details: always\n\n# springdoc\nspringdoc:\n  swagger-ui:\n    path: /swagger-ui.html\n\n# ignore\nignore:\n  uris:\n    - /v3/api-docs/**\n    - /swagger-ui.html\n    - /swagger-ui/**\n    - /actuator/**\n    - /error\n\n# server\nserver:\n  servlet:\n    encoding:\n      charset: UTF